import { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [movieName, setMovieName] = useState("");
  const [movieReview, setMovieReview] = useState("");

  const submitHandler = (e) => {
    e.preventDefault();
  };

  return (
    <form className="main__wrapper">
      <h1>CRUD Application using Node, React andMysql</h1>
      <label>Movie Name</label>
      <input
        type="text"
        name="movieName"
        onChange={(e) => setMovieName(e.target.value)}
      />
      <label>Review</label>
      <input
        type="text"
        name="review"
        onChange={(e) => setMovieReview(e.target.value)}
      />
      <button type="submit" className="mt-3" onSubmit={submitHandler}>
        Submit
      </button>
      {movieName} {movieReview}
    </form>
  );
}

export default App;
