import React, { useState, useRef } from "react";

const Movie = ({ movie, onDelete, onUpdate }) => {
  const textAreaRef = useRef();
  return (
    <div className="card">
      <h1>{movie.movieName}</h1>
      <p>{movie.movieReview}</p>
      <div className="button-flex">
        <button className="delete" onClick={() => onDelete(movie)}>
          Delete
        </button>
        <textarea
          rows="3"
          type="text"
          className="update"
          id="newReview"
          ref={textAreaRef}
        />
        <button
          className="update"
          onClick={() => onUpdate(movie, textAreaRef.current.value)}
        >
          Update
        </button>
      </div>
    </div>
  );
};

export default Movie;
