import { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";
import Movie from "./Movie";

function App() {
  const [movieName, setMovieName] = useState("");
  const [movieReview, setMovieReview] = useState("");
  const [listMovies, setListMovies] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:3001/get").then((res) => {
      console.log(res.data);
      setListMovies(res.data);
    });
  }, []);

  const submitHandler = () => {
    console.log(movieName, movieReview);
    axios
      .post("http://localhost:3001/insert", {
        movieName: movieName,
        movieReview: movieReview,
      })
      .then(() => {
        alert("success");
      });
  };

  return (
    <div className="main__wrapper">
      <h1>CRUD Application using Node, React andMysql</h1>
      <label>Movie Name</label>
      <input
        type="text"
        name="movieName"
        onChange={(e) => setMovieName(e.target.value)}
      />
      <label>Review</label>
      <input
        type="text"
        name="movieReview"
        onChange={(e) => setMovieReview(e.target.value)}
      />
      <button className="mt-3" onClick={submitHandler}>
        Submit
      </button>
      {listMovies.map((movie) => {
        return <Movie movie={movie} key={movie.id} />;
      })}
    </div>
  );
}

export default App;
